function agregarTradeCompletado(entradas, salidas) {
  if (entradas.length > 0 && salidas.length > 0) {
    const Qty = entradas.reduce((sum, t) => sum + Number(t.qty), 0);
    const quoteQty = entradas.reduce((sum, t) => sum + Number(t.quoteQty), 0);
    const avgEntryPrice = Qty > 0 ? entradas.reduce((sum, t) => sum + Number(t.price) * Number(t.qty), 0) / Qty : 0;
    const qtySalida = salidas.reduce((sum, t) => sum + Number(t.qty), 0);
    const avgExitPrice = qtySalida > 0 ? salidas.reduce((sum, t) => sum + Number(t.price) * Number(t.qty), 0) / qtySalida : 0;
    const totalCommission = [...entradas, ...salidas].reduce((sum, t) => sum + Number(t.commission || 0), 0);
    const realizedPnl = salidas.reduce((sum, t) => sum + Number(t.realizedPnl || 0), 0);

    result.push({
      SignalTime: formatFecha(entradas[0].time),
      Side: entradas[0].side === 'BUY' ? 'LONG' : 'SHORT',
      EntryPrice: round2(avgEntryPrice),
      ExitTime: formatFecha(salidas[salidas.length-1].time),
      ExitPrice: round2(avgExitPrice),
      Quantity: round2(quoteQty),
      Commission: round6(totalCommission),
      Qty: round6(Qty),
      realizedPnl: round2(realizedPnl),
      // Opcional: agregar mÃ¡s mÃ©tricas
      NumEntradas: entradas.length,
      NumSalidas: salidas.length,
      Symbol: entradas[0].symbol
    });
  }
}

for (let t of trades) {
  if (Number(t.realizedPnl) === 0) {
    if (bufferSalidas.length > 0) {
      agregarTradeCompletado(bufferEntradas, bufferSalidas);
      bufferEntradas = [];
      bufferSalidas = [];
    }
    bufferEntradas.push(t);
  } else {
    bufferSalidas.push(t);
  }
}

if (bufferEntradas.length > 0 && bufferSalidas.length > 0) {
  agregarTradeCompletado(bufferEntradas, bufferSalidas);
}

let cumPnL = 0;
const resultWithCumPnL = result.map(trade => {
