// 1) Extraemos arrays de precio y volumen
const closes  = items.map(i => i.json.close);
const highs   = items.map(i => i.json.high);
const lows    = items.map(i => i.json.low);
const volumes = items.map(i => i.json.volume);

// 2) EMAs general
function ema(values, period) {
  const k = 2 / (period + 1);
  const out = Array(values.length).fill(undefined);
  const sma = values.slice(0, period).reduce((a, b) => a + b, 0) / period;
  out[period - 1] = sma;
  for (let i = period; i < values.length; i++) {
    out[i] = (values[i] - out[i - 1]) * k + out[i - 1];
  }
  return out;
}

// 3) RSI Wilder
function rsi(values, period) {
  const out = Array(values.length).fill(undefined);
  let gains = 0, losses = 0;
  for (let i = 1; i <= period; i++) {
    const d = values[i] - values[i - 1];
    if (d > 0) gains += d; else losses -= d;
  }
  let avgG = gains / period, avgL = losses / period;
  for (let i = period + 1; i < values.length; i++) {
    const d = values[i] - values[i - 1];
    avgG = ((avgG * (period - 1)) + Math.max(d, 0)) / period;
    avgL = ((avgL * (period - 1)) + Math.max(-d, 0)) / period;
    const rs = avgG / avgL;
    out[i] = 100 - (100 / (1 + rs));
  }
  return out;
}

// 4) Bollinger, VolAvg, ATR
function rollingStats(values, period, multBB) {
  const mb = Array(values.length).fill(undefined);
  const ub = Array(values.length).fill(undefined);
  const lb = Array(values.length).fill(undefined);
  const volAvg = Array(values.length).fill(undefined);
  const atr = Array(values.length).fill(undefined);

  let sum = 0, sumSq = 0, volSum = 0;
  let prevClose = values[0], atrSum = 0;

  for (let i = 0; i < values.length; i++) {
    sum += values[i];
    sumSq += values[i] * values[i];
    volSum += volumes[i];

    // ATR true range
    const tr = Math.max(
      highs[i] - lows[i],
      Math.abs(highs[i] - prevClose),
      Math.abs(lows[i] - prevClose)
    );
    if (i === 0) atrSum = tr;
    else atrSum += tr;

    if (i >= period) {
      sum -= values[i - period];
      sumSq -= values[i - period] * values[i - period];
      volSum -= volumes[i - period];
      atrSum -= Math.max(
        highs[i - period] - lows[i - period],
        Math.abs(highs[i - period] - (i - period - 1 >= 0 ? closes[i - period - 1] : prevClose)),
        Math.abs(lows[i - period] - (i - period - 1 >= 0 ? closes[i - period - 1] : prevClose))
      );
    }

    if (i >= period - 1) {
      const mean = sum / period;
      const varp = (sumSq / period) - (mean * mean);
      const sd = Math.sqrt(varp);
      mb[i] = mean; ub[i] = mean + multBB * sd; lb[i] = mean - multBB * sd;
      volAvg[i] = volSum / period;
      atr[i] = atrSum / period;
    }
    prevClose = values[i];
  }
  return { mb, ub, lb, volAvg, atr };
}

// 5) MACD (12-26-9 por defecto)
function macd(values, fast = 12, slow = 26, signal = 9) {
  const emaFast = ema(values, fast);
  const emaSlow = ema(values, slow);
  const macdLine = values.map((_, i) =>
    emaFast[i] !== undefined && emaSlow[i] !== undefined
      ? emaFast[i] - emaSlow[i]
      : undefined
  );
  const signalLine = ema(macdLine.map(v => v === undefined ? 0 : v), signal);
  const hist = macdLine.map((v, i) =>
    v !== undefined && signalLine[i] !== undefined
      ? v - signalLine[i]
      : undefined
  );
  return { macdLine, signalLine, hist };
}

// 6) ADX, DI+, DI- (14 por defecto)
function adx(highs, lows, closes, period = 14) {
  const len = highs.length;
  const dxArr = Array(len).fill(undefined);
  const adxArr = Array(len).fill(undefined);
  const plusDI = Array(len).fill(undefined);
  const minusDI = Array(len).fill(undefined);

  let trArr = [], pdmArr = [], ndmArr = [];

  for (let i = 1; i < len; i++) {
    const tr = Math.max(
      highs[i] - lows[i],
      Math.abs(highs[i] - closes[i - 1]),
      Math.abs(lows[i] - closes[i - 1])
    );
    trArr.push(tr);

    const pdm = highs[i] - highs[i - 1] > lows[i - 1] - lows[i] && (highs[i] - highs[i - 1]) > 0
      ? highs[i] - highs[i - 1] : 0;
    const ndm = lows[i - 1] - lows[i] > highs[i] - highs[i - 1] && (lows[i - 1] - lows[i]) > 0
      ? lows[i - 1] - lows[i] : 0;
    pdmArr.push(pdm);
    ndmArr.push(ndm);
  }

  function smma(arr, period) {
    let out = Array(arr.length).fill(undefined);
    let sum = arr.slice(0, period).reduce((a, b) => a + b, 0);
    out[period - 1] = sum / period;
    for (let i = period; i < arr.length; i++) {
      out[i] = ((out[i - 1] * (period - 1)) + arr[i]) / period;
    }
    return out;
  }

  const atr = smma(trArr, period);
  const pdi = smma(pdmArr, period).map((v, i) => v !== undefined && atr[i] !== undefined ? 100 * v / atr[i] : undefined);
  const ndi = smma(ndmArr, period).map((v, i) => v !== undefined && atr[i] !== undefined ? 100 * v / atr[i] : undefined);

  for (let i = period - 1; i < atr.length; i++) {
    if (pdi[i] !== undefined && ndi[i] !== undefined) {
      const sum = pdi[i] + ndi[i];
      const dx = sum === 0 ? 0 : 100 * Math.abs(pdi[i] - ndi[i]) / sum;
      dxArr[i + 1] = dx;
      plusDI[i + 1] = pdi[i];
      minusDI[i + 1] = ndi[i];
    }
  }
  // ADX: smoothed average of DX
  const adxVals = smma(dxArr.map(x => x === undefined ? 0 : x), period);
  for (let i = 2 * period - 1; i < adxVals.length; i++) {
    adxArr[i + 1] = adxVals[i];
  }

  return { adxArr, plusDI, minusDI };
}

// 7) SuperTrend (perÃ­odo 10, multiplicador 3 por defecto)
function supertrend(highs, lows, closes, period = 10, mult = 3) {
  const atr = ema(highs.map((_, i) => {
    return Math.max(
      highs[i] - lows[i],
      Math.abs(highs[i] - closes[i - 1] || highs[i] - lows[i]),
      Math.abs(lows[i] - closes[i - 1] || highs[i] - lows[i])
    );
  }), period);

  const basicUpper = highs.map((h, i) => ((h + lows[i]) / 2) + mult * (atr[i] || 0));
  const basicLower = lows.map((l, i) => ((highs[i] + l) / 2) - mult * (atr[i] || 0));
  const superTrend = Array(closes.length).fill(null);

  let prevSuper = null, prevDir = null;

  for (let i = 0; i < closes.length; i++) {
    if (i === 0) {
      superTrend[i] = basicUpper[i];
      prevSuper = basicUpper[i];
      prevDir = -1;
      continue;
    }
    let currSuper;
    if (closes[i - 1] <= prevSuper) {
      currSuper = Math.min(basicUpper[i], prevSuper);
    } else {
      currSuper = basicUpper[i];
    }
    if (closes[i] > currSuper) {
      prevDir = 1;
      prevSuper = basicLower[i];
    } else {
      prevDir = -1;
      prevSuper = currSuper;
    }
    superTrend[i] = prevSuper;
  }
  // SeÃ±al: 1 = tendencia alcista, -1 = bajista
  const trendDir = closes.map((close, i) => (close > superTrend[i] ? 1 : -1));
  return { superTrend, trendDir };
}

