// ----- INDICADORES -----

function calcEMA(valuesArray, period) {
  const multiplier = 2 / (period + 1);
  const result = Array(valuesArray.length).fill(undefined);
  const sma = valuesArray.slice(0, period).reduce((a,b) => a+b, 0) / period;
  result[period - 1] = sma;
  for (let j = period; j < valuesArray.length; j++) {
    result[j] = (valuesArray[j] - result[j-1]) * multiplier + result[j-1];
  }
  return result;
}

function calcRSIWilder(valuesArray, period) {
  const result = Array(valuesArray.length).fill(undefined);
  let gains = 0, losses = 0;
  for (let k = 1; k <= period; k++) {
    const diff = valuesArray[k] - valuesArray[k-1];
    if (diff > 0) gains += diff; else losses -= diff;
  }
  let avgGain = gains / period, avgLoss = losses / period;
  for (let m = period + 1; m < valuesArray.length; m++) {
    const d = valuesArray[m] - valuesArray[m-1];
    avgGain = ((avgGain * (period - 1)) + Math.max(d,0)) / period;
    avgLoss = ((avgLoss * (period - 1)) + Math.max(-d,0)) / period;
    const rs = avgLoss === 0 ? 0 : avgGain / avgLoss;
    result[m] = avgLoss === 0 ? 100 : 100 - (100 / (1 + rs));
  }
  return result;
}

function calcBBVolATR(closeArr, highArr, lowArr, volArr, period, multBB) {
  const bbMidArr = Array(closeArr.length).fill(undefined);
  const bbUpperArr = Array(closeArr.length).fill(undefined);
  const bbLowerArr = Array(closeArr.length).fill(undefined);
  const volAvgArr = Array(closeArr.length).fill(undefined);
  const atrArr = Array(closeArr.length).fill(undefined);

  let sumClose = 0, sumCloseSq = 0, sumVol = 0, prevClose = closeArr[0], sumATR = 0;
  for(let n=0;n<closeArr.length;n++) {
    sumClose += closeArr[n];
    sumCloseSq += closeArr[n]*closeArr[n];
    sumVol += volArr[n];
    const trueRange = Math.max(
      highArr[n]-lowArr[n],
      Math.abs(highArr[n]-prevClose),
      Math.abs(lowArr[n]-prevClose)
    );
    if (n === 0) sumATR = trueRange;
    else sumATR += trueRange;

    if (n >= period) {
      sumClose -= closeArr[n-period];
      sumCloseSq -= closeArr[n-period]*closeArr[n-period];
      sumVol -= volArr[n-period];
      sumATR -= Math.max(
        highArr[n-period]-lowArr[n-period],
        Math.abs(highArr[n-period]-(n-period-1>=0?closeArr[n-period-1]:prevClose)),
        Math.abs(lowArr[n-period]-(n-period-1>=0?closeArr[n-period-1]:prevClose))
      );
    }
    if (n >= period-1) {
      const mean = sumClose/period;
      const stdev = Math.sqrt(sumCloseSq/period-mean*mean);
      bbMidArr[n] = mean;
      bbUpperArr[n] = mean+multBB*stdev;
      bbLowerArr[n] = mean-multBB*stdev;
      volAvgArr[n] = sumVol/period;
      atrArr[n] = sumATR/period;
    }
    prevClose = closeArr[n];
  }
  return {
    mid: bbMidArr,
    upper: bbUpperArr,
    lower: bbLowerArr,
    volAvg: volAvgArr,
    atr: atrArr
  };
}

function calcMACD(valuesArr, fast = 12, slow = 26, signal = 9) {
  const emaFastArr = calcEMA(valuesArr, fast);
  const emaSlowArr = calcEMA(valuesArr, slow);
  const macdArr    = valuesArr.map((_, i) => (
    emaFastArr[i] !== undefined && emaSlowArr[i] !== undefined
      ? emaFastArr[i] - emaSlowArr[i]
      : undefined
  ));
  const signalArr = calcEMA(macdArr.map(v=>v===undefined?0:v), signal);
  const histArr = macdArr.map((v,i)=>(
    v !== undefined && signalArr[i] !== undefined
      ? v - signalArr[i]
      : undefined
  ));
  return { macd: macdArr, signal: signalArr, hist: histArr };
}

function calcADX(highArr, lowArr, closeArr, period=14) {
  const len = highArr.length;
  const dxArr = Array(len).fill(undefined),
        adxArr = Array(len).fill(undefined),
        plusDIarr = Array(len).fill(undefined),
        minusDIarr = Array(len).fill(undefined);

  let trList = [], pdmList = [], ndmList = [];
  for(let i=1;i<len;i++) {
    const tr = Math.max(
      highArr[i] - lowArr[i],
      Math.abs(highArr[i] - closeArr[i-1]),
      Math.abs(lowArr[i] - closeArr[i-1])
    );
    trList.push(tr);
    const pdm = (highArr[i]-highArr[i-1]) > (lowArr[i-1]-lowArr[i]) && (highArr[i]-highArr[i-1])>0 ? highArr[i]-highArr[i-1]:0;
    const ndm = (lowArr[i-1]-lowArr[i]) > (highArr[i]-highArr[i-1]) && (lowArr[i-1]-lowArr[i])>0 ? lowArr[i-1]-lowArr[i]:0;
    pdmList.push(pdm); ndmList.push(ndm);
  }
  function smma(arr, period) {
    let o = Array(arr.length).fill(undefined);
    let sum = arr.slice(0, period).reduce((a,b)=>a+b, 0);
    o[period-1] = sum/period;
    for(let z=period;z<arr.length;z++)
      o[z] = ((o[z-1]*(period-1))+arr[z])/period;
    return o;
  }
  const atrArr = smma(trList, period);
  const pdiArr = smma(pdmList, period).map((v,i)=>v!==undefined && atrArr[i]!==undefined?100*v/atrArr[i]:undefined);
  const ndiArr = smma(ndmList, period).map((v,i)=>v!==undefined && atrArr[i]!==undefined?100*v/atrArr[i]:undefined);
  for(let j=period-1;j<atrArr.length;j++) {
    if (pdiArr[j]!==undefined && ndiArr[j]!==undefined) {
      const sum = pdiArr[j]+ndiArr[j];
      const dx = sum===0 ? 0 : 100*Math.abs(pdiArr[j]-ndiArr[j])/sum;
      dxArr[j+1] = dx;
      plusDIarr[j+1] = pdiArr[j];
      minusDIarr[j+1] = ndiArr[j];
    }
  }
  const adxSmoothed = smma(dxArr.map(x=>x===undefined?0:x),period);
  for(let k=2*period-1;k<adxSmoothed.length;k++) {
    adxArr[k+1] = adxSmoothed[k];
  }
  return { adx: adxArr, plusDI: plusDIarr, minusDI: minusDIarr };
}

function calcSupertrend(highArr, lowArr, closeArr, period=10, mult=3) {
  const atrArr = calcEMA(highArr.map((_, i) => {
    return Math.max(
      highArr[i] - lowArr[i],
      Math.abs(highArr[i] - closeArr[i-1] || highArr[i]-lowArr[i]),
      Math.abs(lowArr[i] - closeArr[i-1] || highArr[i]-lowArr[i])
    );
  }), period);

  const basicUpper = highArr.map((h, i) => ((h + lowArr[i]) / 2) + mult * (atrArr[i] || 0));
  const basicLower = lowArr.map((l, i) => ((highArr[i] + l) / 2) - mult * (atrArr[i] || 0));
  const supertrendArr = Array(closeArr.length).fill(null);
  let prevSuper = null;

  for (let i = 0; i < closeArr.length; i++) {
    if (i === 0) {
      supertrendArr[i] = basicUpper[i];
      prevSuper = basicUpper[i];
      continue;
    }
    let currSuper;
    if (closeArr[i - 1] <= prevSuper) currSuper = Math.min(basicUpper[i], prevSuper);
    else currSuper = basicUpper[i];
    if (closeArr[i] > currSuper) prevSuper = basicLower[i];
    else prevSuper = currSuper;
