function analyzeTechnicalSignal(data, prev_signal = null) {
    const {
        time, open, high, low, close, volume,
        ema9, ema21, ema50, ema200, rsi14,
        bb_mid, bb_up, bb_dn, vol_avg20, atr14,
        macd, macd_signal, macd_hist,
        adx14, di_plus, di_minus,
        supertrend, supertrend_dir
    } = data;

    // ============================================
    // STEP 1: ELIMINATION FILTERS
    // ============================================

    // Missing critical data
    if (!close || !ema9 || !ema21 || !ema50 || !volume || !vol_avg20) {
        return { signal: "NO TRADE", reason: "Missing critical data", score: 0 };
    }

    // Volume filter
    const vol_ratio = volume / vol_avg20;
    if (vol_ratio < 0.7) {
        return { signal: "NO TRADE", reason: "Volume below 70% average", score: 0 };
    }

    // RSI dead zone (without strong momentum)
    if (rsi14 >= 42 && rsi14 <= 58) {
        // Check if other momentum is extremely strong
        const macd_strength = macd && macd_signal ? Math.abs(macd - macd_signal) : 0;
        if (macd_strength < 0.2 && (!adx14 || adx14 < 25)) {
            return { signal: "NO TRADE", reason: "RSI neutral zone without momentum", score: 0 };
        }
    }

    // Manipulation detection - extreme wicks
    const body_size = Math.abs(close - open);
    const range_size = high - low;
    const wick_size = (high - Math.max(open, close)) + (Math.min(open, close) - low);

    if (body_size < 0.4 * range_size && wick_size > 1.5 * atr14) {
        return { signal: "NO TRADE", reason: "Potential manipulation detected", score: 0 };
    }

    // Extreme volatility
    const atr_ratio = atr14 / (atr14 * 0.5); // Assuming normal ATR is half current
    if (atr_ratio > 2) {
        return { signal: "NO TRADE", reason: "Abnormal volatility", score: 0 };
    }

    // ============================================
    // STEP 2: SCORING CALCULATION
    // ============================================

    let trend_score = 0;
    let momentum_score = 0;
    let volume_score = 0;
    let confirmation_score = 0;
    let signal_type = "";
    let context = "";

    // ============================================
    // TREND SCORING (35 points max)
    // ============================================

    // EMA alignment check
    const long_ema_aligned = close > ema9 && ema9 > ema21 && ema21 > ema50;
    const short_ema_aligned = close < ema9 && ema9 < ema21 && ema21 < ema50;

    // EMA200 relationship
    const ema50_200_diff = ema200 ? Math.abs(ema50 - ema200) / ema200 * 100 : 0;

    // LONG trend scoring
    if (close > ema9) {
        if (long_ema_aligned) trend_score += 20;
        else if (close > ema21 && close > ema50) trend_score += 15;
        else if (close > ema21) trend_score += 10;
        else trend_score += 5;

        // EMA200 relationship for LONG
        if (ema200) {
            if (ema50 > ema200 && close > ema200) {
                trend_score += 15;
                context = "Tendencia mayor confirmada";
            } else if (ema50_200_diff < 0.5 && close >= ema200) {
                trend_score += 12;
                context = "Reversal anticipado fuerte";
            } else if (ema50_200_diff < 1.0 && close >= ema200) {
                trend_score += 8;
                context = "Reversal anticipado moderado";
            } else if (close > ema200) {
                trend_score += 5;
            }
        }
        signal_type = "LONG";
    }

    // SHORT trend scoring
    else if (close < ema9) {
        if (short_ema_aligned) trend_score += 20;
        else if (close < ema21 && close < ema50) trend_score += 15;
        else if (close < ema21) trend_score += 10;
        else trend_score += 5;

        // EMA200 relationship for SHORT
        if (ema200) {
            if (ema50 < ema200 && close < ema200) {
                trend_score += 15;
                context = "Tendencia mayor confirmada";
            } else if (ema50_200_diff < 0.5 && close <= ema200) {
                trend_score += 12;
                context = "Reversal anticipado fuerte";
            } else if (ema50_200_diff < 1.0 && close <= ema200) {
                trend_score += 8;
                context = "Reversal anticipado moderado";
            } else if (close < ema200) {
                trend_score += 5;
            }
        }
        signal_type = "SHORT";
    }

    // ============================================
    // MOMENTUM SCORING (25 points max)
    // ============================================

    if (signal_type === "LONG") {
        // RSI scoring for LONG
        if (rsi14 >= 65) momentum_score += 15;
        else if (rsi14 >= 60) momentum_score += 12;
        else if (rsi14 >= 58) momentum_score += 8;
        else if (rsi14 >= 55) momentum_score += 4;

        // MACD scoring for LONG
        if (macd && macd_signal) {
            if (macd > macd_signal && macd_hist > 0.1) momentum_score += 10;
            else if (macd > macd_signal && macd_hist > 0) momentum_score += 7;
            else if (macd > macd_signal) momentum_score += 5;
        }
    } else if (signal_type === "SHORT") {
        // RSI scoring for SHORT
        if (rsi14 <= 35) momentum_score += 15;
        else if (rsi14 <= 40) momentum_score += 12;
        else if (rsi14 <= 42) momentum_score += 8;
        else if (rsi14 <= 45) momentum_score += 4;

        // MACD scoring for SHORT
        if (macd && macd_signal) {
            if (macd < macd_signal && macd_hist < -0.1) momentum_score += 10;
            else if (macd < macd_signal && macd_hist < 0) momentum_score += 7;
            else if (macd < macd_signal) momentum_score += 5;
        }
    }

    // ============================================
    // VOLUME SCORING (20 points max)
    // ============================================

    // Volume ratio scoring
    if (vol_ratio >= 1.2) volume_score += 12;
    else if (vol_ratio >= 1.0) volume_score += 10;
    else if (vol_ratio >= 0.9) volume_score += 8;
    else if (vol_ratio >= 0.8) volume_score += 6;

    // Volume trend scoring (simplified - would need previous volumes)
    // For now, assume moderate scoring
    volume_score += 5;

    // ============================================
    // CONFIRMATION SCORING (20 points max)
    // ============================================

    // Bollinger Bands
    if (signal_type === "LONG" && close > bb_mid) confirmation_score += 5;
    else if (signal_type === "SHORT" && close < bb_mid) confirmation_score += 5;

    // ADX/DI scoring
    if (adx14 && di_plus && di_minus) {
        if (signal_type === "LONG" && di_plus > di_minus) {
            if (adx14 >= 25) confirmation_score += 8;
            else if (adx14 >= 20) confirmation_score += 6;
            else if (adx14 >= 18) confirmation_score += 4;
        } else if (signal_type === "SHORT" && di_minus > di_plus) {
            if (adx14 >= 25) confirmation_score += 8;
            else if (adx14 >= 20) confirmation_score += 6;
            else if (adx14 >= 18) confirmation_score += 4;
        }
    }

    // SuperTrend
    if (supertrend_dir) {
        if ((signal_type === "LONG" && supertrend_dir === 1) || 
            (signal_type === "SHORT" && supertrend_dir === -1)) {
            confirmation_score += 4;
        }
    }

    // ATR relative strength (simplified)
    confirmation_score += 2;

    // ============================================
    // STEP 3: FINAL DECISION
    // ============================================

    const total_score = Math.min(trend_score, 35) + Math.min(momentum_score, 25) + 
                       Math.min(volume_score, 20) + Math.min(confirmation_score, 20);

    let signal_strength = "";
    let sl_tp_ratio = "";
    let sl_price = 0;
    let tp_price = 0;

    if (total_score >= 75) {
        signal_strength = "FUERTE";
        sl_tp_ratio = "1:2.5";
    } else if (total_score >= 60) {
        signal_strength = "MODERADA";
        sl_tp_ratio = "1:1.5";
    } else {
        return { 
            signal: "NO TRADE", 
            reason: `Score insuficiente: ${total_score}/100`, 
            score: total_score 
        };
    }

    // Calculate SL/TP prices
    const sl_distance = Math.max(close * 0.005, atr14 * 0.3);
    const tp_multiplier = signal_strength === "FUERTE" ? 2.5 : 1.5;

    if (signal_type === "LONG") {
        sl_price = Math.round((close - sl_distance) * 100) / 100;
        tp_price = Math.round((close + (sl_distance * tp_multiplier)) * 100) / 100;
    } else {
        sl_price = Math.round((close + sl_distance) * 100) / 100;
        tp_price = Math.round((close - (sl_distance * tp_multiplier)) * 100) / 100;
    }

    // Check if signal is different from previous
    const current_signal = `${signal_type} ${signal_strength}`;
    if (prev_signal && prev_signal === current_signal) {
        return { signal: "", reason: "Same signal as previous", score: total_score };
    }

    // Build response
    const score_breakdown = `T:${Math.min(trend_score, 35)}, M:${Math.min(momentum_score, 25)}, V:${Math.min(volume_score, 20)}, C:${Math.min(confirmation_score, 20)}`;

    const technical_description = `Close ${close} vs EMA9(${ema9}), RSI ${rsi14}, MACD ${macd?.toFixed(2) || 'N/A'}, Vol ${Math.round(vol_ratio * 100)}% avg`;

    return {
        signal: `${signal_type} ${signal_strength}`,
        score: total_score,
        score_breakdown: score_breakdown,
        technical_description: technical_description,
        context: context,
        sl_tp_ratio: sl_tp_ratio,
        sl_price: sl_price,
        tp_price: tp_price,
        formatted_response: `${signal_type} ${signal_strength}. Score: ${total_score}/100 (${score_breakdown}). ${technical_description}. Contexto: ${context}. Ratio SL-TP: ${sl_tp_ratio} (SL=${sl_price}, TP=${tp_price})`
    };
}

